import React, { Component } from 'react';
import {
  Text,
  View,
  StyleSheet,
  Button,
  ScrollView,
  RefreshControl,
  Dimensions,
  SectionList,
  TouchableHighlight,
  FlatList,
  TouchableOpacity,
  Image,
  Platform,
  ImageBackground,
  ActivityIndicator,
  WebView,
} from 'react-native';

import { Ionicons, MaterialIcons } from '@expo/vector-icons';

import { Dropdown } from 'react-native-material-dropdown';


import { Font } from 'expo-font';

import HTML from 'react-native-render-html';

import colors from '../../styles/colors';
import fonts from '../../styles/fonts';

import moment from 'moment';
import Dialog, { DialogContent } from 'react-native-popup-dialog';


class BlogScreen extends Component {
  constructor(props) {
    super(props);
    this.state = { fontLoaded: false, isLoading: true, isLoadingCats: true, categories: [], dataSource: [] }
    this.state.visibleCategory = false;
    this.state.categoryName = "Filteri";
    this.state.category = "";
  }
  componentDidMount() {
    this.loadAssetsAsync()
    this.downloadData();
  }

  onChangeCategoryPress(value) {
    this.setState(previousState => (
      { category: "" + value.id, categoryName: value.title, visibleCategory: false }
    ))
    this.downloadData();
  }

  downloadData() {
    fetch('https://api.360bih.ba/api/blog_posts')
      .then((response) => response.json())
      .then((responseJson) => {

        this.setState({
          isLoading: false,
          dataSource: responseJson,
          refreshing: false
        }, function () {
        });

      })
      .catch((error) => {
        console.error(error);
      });

    fetch('https://api.360bih.ba/api/categories')
      .then((response) => response.json())
      .then((responseJson) => {

        this.setState({
          isLoading: false,
          categories: responseJson,
          refreshing: false
        }, function () {

        });

      })
      .catch((error) => {
        console.error(error);
      });
  }
  loadAssetsAsync = async () => {
    await Font.loadAsync({
      "Roboto-Regular": require("../../assets/fonts/rregular.ttf"),
      "Roboto-Medium": require("../../assets/fonts/rmedium.ttf"),
      "Roboto-Light": require("../../assets/fonts/rlight.ttf"),
      "Roboto-Bold": require("../../assets/fonts/rbold.ttf"),
    })
    this.setState({ fontLoaded: true })
  }

  _openBlog = article => {
    const { navigate } = this.props.navigation;
    navigate({
      routeName: 'SingleBlog',
      params: { ...article },
    });
  };

  renderRowTwo = ({ item }) => (
    <View style={styles.cardContainer} backgroundColor="#fff">
      <TouchableOpacity
        key={item.id}
        style={styles.itemTwoContainer}
        onPress={() => this._openBlog(item)}
      >
        <View style={styles.itemTwoContainer}>
          <View style={styles.itemTwoContent}>
            <Image style={styles.itemTwoImage} source={{ uri: item.image.content_url }} />
          </View>
          <View style={styles.itemTwoContainer2}>
            <View style={{ display: "flex", flexDirection: "row", justifyContent: "space-between", marginTop: 16, marginBottom: 8 }}>
              <Text
                style={styles.itemOneSubTitle} numberOfLines={1}>
                {item.category.title}
              </Text>
              <Text
                style={styles.itemOneSubTitleRight} numberOfLines={1}>
                {moment(item.created_at).format('DD.MM.YYYY.')}
              </Text>
            </View>

            <Text style={{ fontFamily: fonts.primaryBold, color: "#4A4A4A", fontSize: 16, marginBottom: 8 }} numberOfLines={2}>
              {item.title}
            </Text>
            <HTML style={{ marginTop: 12 }} html={'<k>' + item.content + '</k>'} renderers={{
              k: (htmlAttribs, children, convertedCSSStyles, passProps) => <Text textAlign='justify' numberOfLines={3} style={{ color: "#4A4A4A", fontSize: 16 }}>{children}</Text>,
            }} />
          </View>
        </View>

      </TouchableOpacity>
    </View>
  );


  _onRefresh = () => {
    this.setState({ refreshing: true });
    this.downloadData()
  }

  _getRenderItemFunction = () =>
    [this.renderRowTwo,][0];


  render() {
    let data = []
    let copyDataSource = [];
    this.state.dataSource.forEach(item => {
      if (this.state.category && this.state.category.length > 0 && item.category) {
        if (item.category.id === this.state.category) {
          copyDataSource.push(item);
        }
      } else {
        copyDataSource.push(item);
      }
    })
    this.state.dataSource = copyDataSource;
    // console.log(copyDataSource);
    if(this.state.categories)
    {
      this.state.categories.forEach(el =>{
        el.value = el.title
      })
    }
    if (!this.state.fontLoaded) {
      return <View />
    }
    if (this.state.isLoading) {
      return (
        <View style={{ flex: 1, padding: 20 }}>
          <ActivityIndicator />
        </View>
      )
    } else {
      const groupedData = copyDataSource;

      return (
        <ScrollView style={styles.container}
          refreshControl={
            <RefreshControl
              colors={["#0C8BB2", "#0C8BB2", "#0C8BB2"]}
              refreshing={this.state.refreshing}
              onRefresh={this._onRefresh}
            />
          }>
          <View style={styles.cardContainer}>
            <Dialog style={{ marginLeft: 16, marginRight: 16 }}
              visible={this.state.visibleCategory}
              onTouchOutside={() => {
                this.setState({ visibleCategory: false });
              }}
            >
              <DialogContent style={{ paddingTop: 16, height: 250 }}>
                <ScrollView style={{ height: 250 }}>
                  {this.state.categories.map((city, index) => {
                    return (
                      <TouchableOpacity onPress={() => {
