import React, { Component } from 'react';
import {
  Text,
  View,
  StyleSheet,
  Button,
  ScrollView,
  RefreshControl,
  Dimensions,
  SectionList,
  TouchableHighlight,
  FlatList,
  TouchableOpacity,
  Image,
  Platform,
  ImageBackground,
  TextInput,
  Picker,
  Linking,
  WebView,
  Clipboard
} from 'react-native';

import {
  CheckBox
} from 'react-native-elements'
import { Ionicons, MaterialIcons, FontAwesome } from '@expo/vector-icons';

import colors from '../styles/colors';
import fonts from '../styles/fonts';

import { Dropdown } from 'react-native-material-dropdown';


import moment from 'moment';

import { Font } from 'expo-font';

import Dialog, { DialogContent } from 'react-native-popup-dialog';

import MapView from 'react-native-maps';
import Marker from 'react-native-maps';

import HTML from 'react-native-render-html';

class SingleBlog extends Component {

  render() {
    const blog = this.props.navigation.state.params;

    console.log(blog)
    return (
      <ScrollView>
        <ImageBackground style={{ height: 220 }} source={{ uri: blog.image.content_url }}>
          <View style={{ display: "flex", flexDirection: "row", justifyContent: "center", width: Dimensions.get('screen').width }}>
            <Text style={{ color: "#ffffff", fontSize: 24, fontFamily: fonts.primaryBold, marginTop: 107 }}>

            </Text>
          </View>
        </ImageBackground>
        <View style={{ display: "flex", flexDirection: "row", width: Dimensions.get('screen').width }}>
          <Text style={{ color: "#4A4A4A", fontSize: 16, marginLeft: 16, marginTop: 16 }}>
            {moment(blog.created_at).format('DD MMM YYYY')}
          </Text>
          <Text style={{ color: "#4A4A4A", fontSize: 16, marginLeft: 24, marginTop: 16 }}>
            {moment(blog.created_at).format('hh:mmA')}
          </Text>
          <Text style={{ color: "#0C8BB2", fontSize: 16, marginLeft: 24, marginTop: 16 }}>
            {blog.locations && blog.locations.length > 0 ? "#" + blog.locations[0].city.name : ""}
          </Text>
        </View>
        <View
          style={{
            marginLeft: 16,
            marginRight: 16,
            marginTop: 16,
            borderBottomColor: '#D1D3D4',
            borderBottomWidth: StyleSheet.hairlineWidth,
          }}
        />

        <View style={{ margin: 16 }}>
          <HTML html={blog.content} imagesMaxWidth={Dimensions.get('window').width - 32} />
        </View>

        <View
          style={{
            marginLeft: 16,
            marginRight: 16,
            marginTop: 16,
            borderBottomColor: '#D1D3D4',
            borderBottomWidth: StyleSheet.hairlineWidth,
          }}
        />

        <View style={{ display: "none", flexDirection: "row", width: Dimensions.get('screen').width, marginTop: 16 }}>
          <Text style={{ color: "#9B9B9B", fontSize: 14, marginLeft: 16, fontFamily: fonts.primaryBold, marginTop: 8 }}>
            Podijeli
            </Text>
          <TouchableOpacity style={{ marginLeft: 16 }} onPress={() => Linking.openURL('https://www.facebook.com/sharer/sharer.php?u=https://stage.360bih.ba/blog/' + blog.slug)}>
            <FontAwesome name="facebook-square" size={36} color="#9B9B9B" />
          </TouchableOpacity>
          <TouchableOpacity style={{ marginLeft: 24 }} onPress={() => Linking.openURL('https://www.instagram.com/' + blog.locations && blog.locations.length > 0 ? blog.locations[0].instagram : "")}>
            <FontAwesome name="instagram" size={36} color="#9B9B9B" />
          </TouchableOpacity>
          <TouchableOpacity style={{ marginLeft: 24 }} onPress={() => Clipboard.setString('https://stage.360bih.ba/blog/' + blog.slug)}>
            <FontAwesome name="clipboard" size={36} color="#9B9B9B" />
          </TouchableOpacity>
        </View>



      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  }
});

export default SingleBlog;
